package com.bizitglobal.tarjetafiel.contabilidad.exception;
/**
*	Lanzada cuando el objeto no existe en la base de datos.
*/
public class EjercicioCreateException extends Exception {
		/**
	 * 
	 */
	private static final long serialVersionUID = 5678636922467367315L;
		
	private String mensajeDeError;
		
		public EjercicioCreateException(String mensaje) {
			mensajeDeError = mensaje;
		}
		
		public EjercicioCreateException() {
			// TODO Auto-generated constructor stub
		}

		public String getMensajeDeError() {
			return mensajeDeError;
		}

		public void setMensajeDeError(String mensajeDeError) {
			this.mensajeDeError = mensajeDeError;
		}

}

